mapper.update:
update books set
booksname=#{booksname} , image=#{image} , cateid=#{cateid}
,
publisher=#{publisher} , author=#{author} , addtime=#{addtime} ,
storage=#{storage} , lendnum=#{lendnum} , weizhi=#{weizhi} ,
contents=#{contents} where booksid=#{booksid}

mapper.select:
select a.* , b.catename from books a , cate b where 1=1 and a.cateid =
b.cateid

	and a.booksname = #{booksname}


	and a.image = #{image}


	and a.cateid = #{cateid}


	and a.publisher = #{publisher}


	and a.author = #{author}


	and a.addtime = #{addtime}


	and a.storage = #{storage}


	and a.lendnum = #{lendnum}


	and a.weizhi = #{weizhi}


	and a.contents = #{contents}
