mapper.update:
update orders set ordercode=#{ordercode} , usersid=#{usersid} ,
booksid=#{booksid} , status=#{status} , orderdate=#{orderdate} ,
thestart=#{thestart} , theend=#{theend} where ordersid=#{ordersid}

mapper.select:
select a.* , b.username , c.booksname from orders a , users b , books
c where 1=1 and a.usersid = b.usersid and a.booksid = c.booksid

	and a.ordercode = #{ordercode}


	and a.usersid = #{usersid}


	and a.booksid = #{booksid}


	and a.status = #{status}


	and a.orderdate = #{orderdate}


	and a.thestart = #{thestart}


	and a.theend = #{theend}
