mapper.update:
update complains set usersid=#{usersid} , contents=#{contents} ,
addtime=#{addtime} , status=#{status} , reps=#{reps} where
complainsid=#{complainsid}

mapper.select:
select a.* , b.username from complains a , users b where 1=1 and
a.usersid = b.usersid

	and a.usersid = #{usersid}


	and a.contents = #{contents}


	and a.addtime = #{addtime}


	and a.status = #{status}


	and a.reps = #{reps}
